import React, { useEffect, useState } from "react";
import './App.css';
import Web3 from 'web3';


function App() {

  const [web3API, setWeb3API] = useState({
    provider: null,
    web3: null,
  }) 

  const [accounts, setAccount, setBalance] = useState(null)


  useEffect(() => {
    const loadProvider = async () => {
      // with metamask, we have access to window.ethereum & window.web3
      // metamask injects a global API into the website 
      // this API allows websites to request users, accounts, read data to blockchain
      // sign messages and transactions
      console.log(window.web3);
      console.log(window.ethereum);

      let provider;
      if (window.ethereum) {
        provider = window.ethereum;

        try{
          await provider.enable()  // enable metamask
        } catch {
          console.error('User denied accout access')
        }

      } else if (window.web3) {
        provider = window.web3.currentProvider;
      } else if (!process.env.production) {
        provider = new Web3.providers.HttpProvider('http://localhost:7545')
      }

      // create new instance of web3
      setWeb3API({
        web3: new Web3(provider),
        provider
      })
    }

    loadProvider();
  }, [])

  console.log(web3API.web3);



  useEffect(() => {
    const getAccounts = async () => {
      const accounts = await web3API.web3.eth.getAccounts();  // gets array of accounts 
      setAccount(accounts[0])
    }

    const getBalance = async () => {
      const balance = await web3API.web3.eth.getAccounts();  // gets array of accounts 
      setBalance(balance[0])
    }

    if (web3API.web3) {
      getAccounts()  // getAccounts() will only be initialized when you have web3API object
    }
  }, [web3API.web3])

  return (
    <div className="faucet-wrapper">
      <div className="faucet">
        <span>Account: </span>
        <h1> {accounts ? accounts : 'not connected'} </h1>
        <div className="balance-view is-size-2">
          Current Balance: <strong>10</strong> ETH
        </div>
        <button className="btn mr-2" onClick={ async () => {
          const accounts = await window.ethereum.request({method: "eth_requestAccounts"});
          console.log(accounts)
        }}>Enable Ethereum</button>
        <button className="btn mr-2">Donate</button>
        <button className="btn">Withdraw</button>
      </div>
    </div>
  );
}

export default App;
